import React from "react";
import ExpandableSection from "./ExpandableSection";

export const EquipmentList = ({ equipments }) => {
  return (
    <div className="List-Destop">
      <ExpandableSection title="Équipements">
        <div>
          {/* Afficher le premier équipement dans une classe */}
          <p className="EquipmentGroup1">{equipments[0]}</p>
          {/* Afficher les autres équipements dans une autre classe */}
          {equipments.slice(1).map((equipment, index) => (
            <p key={index} className="EquipmentGroup2">{equipment}</p>
          ))}
        </div>
      </ExpandableSection>
    </div>
  );
};
