// Importez les bibliothèques nécessaires
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import data from "../data/logements.json";
import { NotFound } from './NotFound';

export const Accommodation = () => {
  // Récupérez l'ID du logement à partir des paramètres de l'URL
  const { id } = useParams();

  // Utilisez l'ID pour trouver le bon logement dans le fichier JSON
  const [accommodation, setAccommodation] = useState(null);
  useEffect(() => {
    const foundAccommodation = data.find((item) => item.id === id);
    setAccommodation(foundAccommodation);
  }, [id]);

  // Si le logement n'est pas encore trouvé, affichez un message de chargement
  if (!accommodation) {
    return <div>Chargement...</div>;
  }

  // Affichez les détails du logement trouvé ou la page d'erreur 404 si le logement n'est pas trouvé
  return accommodation ? (
    <div>
      {/* Votre code existant pour afficher les détails du logement */}
    </div>
  ) : (
    // Affichez la page d'erreur 404 si le logement n'est pas trouvé
    <NotFound />
  );
};
