// src/pages/Accommodation.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import accommodationsData from '../logements.json';

export const Accommodation = () => {
  const { id } = useParams();
  const [accommodation, setAccommodation] = useState(null);

  useEffect(() => {
    const selectedAccommodation = accommodationsData.find((item) => item.id === id);
    setAccommodation(selectedAccommodation);
  }, [id]);

  // Affiche un message de chargement tant que les données ne sont pas chargées
  if (!accommodation) {
    return <div>Chargement des données...</div>;
  }

  // Les données sont chargées, vous pouvez maintenant les afficher
  return (
    <div>
      <h1>{accommodation.title}</h1>
      // Ajoutez d'autres éléments pour afficher les détails du logement
    </div>
  );
};
