import React, { useState } from 'react';

// Ce composant ExpandableSection a deux props: title et children.
const ExpandableSection = ({ title, children }) => {
  // Nous utilisons l'état 'isOpen' pour suivre si la section est ouverte ou fermée.
  const [isOpen, setIsOpen] = useState(false);

  // Cette fonction sera appelée lorsque l'utilisateur cliquera sur le titre.
  const handleClick = () => {
    // Chaque fois que cette fonction est appelée, nous inversons la valeur de 'isOpen'.
    setIsOpen(!isOpen);
  };

  return (
    <div>
      {/* Nous affichons le titre et ajoutons la fonction 'handleClick' comme gestionnaire d'événements 'onClick'. */}
      <h3 onClick={handleClick} style={{ cursor: 'pointer' }}>
        {title}
      </h3>
      {/* Si 'isOpen' est vrai, nous affichons les éléments enfants passés en tant que props. */}
      {isOpen && <div>{children}</div>}
    </div>
  );
};

export default ExpandableSection;
