import React from "react";
import ExpandableSection from "./ExpandableSection";

export const EquipmentList = ({ equipments }) => {
  const firstEquipment = equipments.shift(); // Retirer le premier équipement du tableau

  return (
    <div className="List-Destop">
      <ExpandableSection title="Équipements">
        <div>
          {/* Créer un groupe pour le premier équipement */}
          <div className="EquipmentGroup1">
            <p>{firstEquipment}</p>
          </div>
          {/* Créer un groupe pour les autres équipements */}
          <div className="EquipmentGroup2">
            {equipments.map((equipment, index) => (
              <p key={index}>{equipment}</p>
            ))}
          </div>
          {/* Créer un groupe pour le dernier équipement */}
          <div className="EquipmentGroup3">
            <p>{equipments[equipments.length - 1]}</p>
          </div>
        </div>
      </ExpandableSection>
      {/* Ajouter une ligne horizontale */}
      <hr />
    </div>
  );
};
