import React from "react";
import ExpandableSection from "./ExpandableSection";

export const EquipmentList = ({ equipments }) => {
  return (
    <div className="List-Destop">
      <ExpandableSection title="Équipements">
        <div>
          {/* Divisez la chaîne en un tableau de mots en utilisant la méthode split */}
          {equipments.split(' ').map((equipment, index) => (
            // Affichez chaque mot dans un élément <p> avec une clé unique basée sur l'index
            <p key={index}>{equipment}</p>
          ))}
        </div>
      </ExpandableSection>
    </div>
  );
};
