// Accommodation.js
import React from 'react';
import { useParams } from 'react-router-dom';
import accommodations from '../data/logements.json';

export const Accommodation = () => {
  const { id } = useParams(); // Récupérez l'ID du logement à partir des paramètres de l'URL
  const accommodation = accommodations.find(acc => acc.id === id); // Trouvez le logement correspondant à l'ID

  if (!accommodation) {
    return <div>Logement introuvable</div>;
  }

  // Affichez les informations du logement
  return (
    <div>
      <h1>{accommodation.title}</h1>
      <p>{accommodation.description}</p>
      {/* Vous pouvez ajouter d'autres informations ici, comme les images, l'hôte, la localisation, etc. */}
    </div>
  );
};
