// Importe les modules nécessaires
import React, { useState } from 'react';
import flecheGauche from '../img/FlecheGauche.png'; // Importe l'image de la flèche gauche
import flecheDroit from '../img/FlecheDroit.png'; // Importe l'image de la flèche droite

// Déclare le composant Carousel
const Carousel = ({ pictures }) => {
  // Reçoit un tableau "pictures" avec les URL des images

  // Crée un état "index" pour stocker l'index de l'image actuelle, initialisé à 0
  const [index, setIndex] = useState(0);

  // Calcule le nombre total d'images dans le tableau
  const totalPictures = pictures.length - 1;

  // Fonction pour passer à l'image précédente
  const onPreviousBtnClick = () => {
    // Si l'index est à 0, passe à la dernière image
    if (index === 0) setIndex(pictures.length - 1);
    // Sinon, décrémente l'index pour afficher l'image précédente
    else setIndex(index - 1);
  };

  // Fonction pour passer à l'image suivante
  const onNextBtnClick = () => {
    // Si l'index est à la dernière image, retourne à la première image
    if (index === pictures.length - 1) setIndex(0);
    // Sinon, incrémente l'index pour afficher l'image suivante
    else setIndex(index + 1);
  };

  // Rendu du composant
  return (
    <div className="carousel">
      {/* Conteneur de l'image actuelle */}
      <div className="div-image">
        {/* Affiche l'image à l'index actuel */}
        <img
          src={pictures[index]}
          className="classImage"
          key={"car-" + index}
          alt={"photo " + index}
        />
      </div>

      {/* Affiche les boutons de navigation si le tableau d'images contient plus d'une image */}
      {totalPictures > 0 && (
        <div className="div-boutons">
          {/* Bouton pour naviguer vers l'image précédente */}
          <button onClick={onPreviousBtnClick}>
            <img
              src={flecheGauche}
              className="classFlecheGauche"
              alt={"flèche gauche pour changer de photo " + index}
            />
          </button>
          {/* Bouton pour naviguer vers l'image suivante */}
          <button onClick={onNextBtnClick}>
            <img
              src={flecheDroit}
              className="classFlecheDroit"
              alt={"flèche droite pour changer de photo " + index}
            />
          </button>
        </div>
      )}

      {/* Affiche le compteur d'images si le tableau d'images contient plus d'une image */}
      {totalPictures > 0 && (
        <div className="div-compteur">
          {/* Compteur d'images : affiche l'index actuel + 1 / nombre total d'images + 1 */}
          <p className="compteurImages">{index + 1}/{totalPictures + 1}</p>
        </div>
      )}
    </div>
  );
};

// Exporte le composant Carousel par défaut
export default Carousel;
