// Banner.js
import '../Styles/Banner.css';

// Ajoutez la prop "text" au composant Banner
export const Banner = ({ image }) => {
  const backgroundImage = image === "home" ? "img-Home" : "img-About";

  return (
    <div className={`banner ${backgroundImage}`}>
      {/* Utilisez deux éléments <p> pour afficher le texte */}
      <p className='banner-text'>Chez vous,</p>
      <p className='banner-text'>Partout et ailleurs</p>
    </div>
  );
};
