// Importer la bibliothèque React
import React from "react";

// Déclarer le composant HostDetails qui prend en paramètre un objet contenant la propriété "host"
export const HostDetails = ({ host }) => {
  // Retourner un élément div contenant les détails de l'hôte
  return (
    <div>
      // Afficher un titre h3 "Hôte"
      <h3>Hôte</h3>
      // Afficher le nom de l'hôte
      <p>{host.name}</p>
      // Afficher l'image de l'hôte avec un attribut alt décrivant l'image
      <img src={host.picture} alt={`Photo de l'hôte ${host.name}`} />
    </div>
  );
};
