import React, { useState, useEffect } from 'react';
import { Article } from './Article';
// import accommodations from '../data/logements.json'; // Vous n'avez plus besoin de cette ligne

export const Section = () => {
  // Créez un état pour stocker les logements
  const [accommodations, setAccommodations] = useState([]);

  // Chargez les logements depuis le backend avec fetch
  useEffect(() => {
    const fetchAccommodations = async () => {
      try {
        const response = await fetch('URL_DU_BACKEND/logements');
        const data = await response.json();
        setAccommodations(data);
      } catch (error) {
        console.error('Erreur lors de la récupération des logements:', error);
      }
    };

    fetchAccommodations();
  }, []);

  return (
    <section className="Section">
      {
        accommodations.map(accommodation => (
          // Passez l'ID du logement en tant que prop
          <Article key={accommodation.id} id={accommodation.id} title={accommodation.title} cover={accommodation.cover} />
        ))
      }
    </section>
  );
}
