import "./Article.css"

// Ajout de props pour le titre et l'image de l'article
export const Article = ({ title, cover }) => {
    return (
      <a href="#">
      <article className="Article">
          <img src={cover} alt={title} className="Article-image" /> {/* Affiche l'image */}
          <h3 className="Article-title">{title}</h3> {/* Affiche le titre */}
      </article>
      </a>
    );
}
