// EquipmentList.js
import React from "react";
import ExpandableSection from "./ExpandableSection";

// Définissez le composant EquipmentList en prenant les équipements, 
// isOpen et handleClick comme props
export const EquipmentList = ({ equipments, isOpen, handleClick }) => {
  return (
    // Créez un conteneur pour le composant EquipmentList
    <div className="List-Destop">
      {/* 
        Passez les props isOpen et handleClick à ExpandableSection 
        pour gérer l'état d'ouverture/fermeture 
      */}
      <ExpandableSection
        title="Équipements"
        isOpen={isOpen}
        handleClick={handleClick}
      >
        {/* 
          Affichez les équipements dans une liste. 
          Utilisez .map() pour parcourir les équipements et créer un élément <p> pour chacun d'eux.
        */}
        <div>
          <div>
            {equipments.map((equipment, index) => (
              <p key={index}>{equipment}</p>
            ))}
          </div>
        </div>
      </ExpandableSection>
    </div>
  );
};
